package com.ssafy.foodfind.ui.home

import android.view.LayoutInflater
import android.view.ViewGroup
import androidx.recyclerview.widget.RecyclerView
import com.bumptech.glide.Glide
import com.ssafy.foodfind.databinding.BannerItemBinding

class BannerAdapter : RecyclerView.Adapter<BannerAdapter.BannerViewHolder>() {

    private val bannerLinks = listOf(
        "https://img.siksinhot.com/place/1668473214409123.jpg",
        "data:image/jpeg;base64,",
    )

    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): BannerViewHolder {
        val binding = BannerItemBinding.inflate(LayoutInflater.from(parent.context), parent, false)
        return BannerViewHolder(binding)
    }

    override fun onBindViewHolder(holder: BannerViewHolder, position: Int) {
        val bannerLink = bannerLinks[position]
        holder.bind(bannerLink)
    }

    override fun getItemCount(): Int {
        return bannerLinks.size
    }

    inner class BannerViewHolder(private val binding: BannerItemBinding) : RecyclerView.ViewHolder(binding.root) {
        fun bind(bannerLink: String) {
            Glide.with(binding.root)
                .load(bannerLink)
                .into(binding.bannerImage)
        }
    }
}